mapper.update:
update category_brand set
brand_id = #{brandId}, category_id = #{categoryId}, update_time = now()
where id = #{id}

mapper.select:
select cb.*, c.name as categoryName, b.name as brandName, b.logo from category_brand cb join brand b on cb.brand_id = b.id join category c on cb.category_id = c.id
and cb.brand_id = #{brandId}
and cb.category_id = #{categoryId}
and cb.is_deleted = 0 and c.is_deleted = 0 and b.is_deleted = 0
order by cb.id desc
